	BNO
	,TITLE
	,CONTENT
	,WRITER
) VALUES (
	SEQ_BOARD.NEXTVAL
	,#{title}
	,#{content}
	,#{writer}
)


	SELECT SEQ_BOARD.NEXTVAL FROM DUAL


INSERT INTO TBL_BOARD(
	BNO
	,TITLE
	,CONTENT
	,WRITER
) VALUES (
	#{bno}
	,#{title}
	,#{content}
	,#{writer}
)

mapper.update:
UPDATE TBL_BOARD SET
	TITLE = #{title}
	,CONTENT = #{content}
	,WRITER = #{writer}
	,UPDATEDATE = sysdate
WHERE BNO = #{bno}

UPDATE TBL_BOARD SET
	REPLYCNT = replycnt + #{amount}
WHERE BNO = #{bno}
