mapper.insert:
insert into user(
	name,
	password
) values (
	#{name},
	#{password}
)

mapper.select:
select
	idx,
	name
from user
where name = #{name}
and password = #{password}

mapper.update:
update user set
	sessionkey = #{sessionkey},
	sessionlimit = #{sessionlimit}
where name = #{name}

mapper.select:
select * from user
where sessionkey = #{sessionkey}
and sessionlimit > now()
